    public CrashManager initializeCrashManager(String str, String str2) {
        SentryEndpointConfig sentryEndpointConfig;
        if (isAndroidTrial()) {
            sentryEndpointConfig = isAndroidChromebook() ? this.mSentryEndpointAndroidChromebookTrial : this.mSentryEndpointAndroidTrial;
        } else if (isAndroidChromebook()) {
            sentryEndpointConfig = this.mSentryEndpointAndroidChromebook;
        } else if (isAndroidAmazon()) {
            sentryEndpointConfig = this.mSentryEndpointAndroidAmazon;
        } else if (isEduMode()) {
            sentryEndpointConfig = this.mSentryEndpointEDU;
        } else if (isPublishBuild()) {
            sentryEndpointConfig = this.mSentryEndpointBedrockPublish;
        } else {
            sentryEndpointConfig = this.mSentryEndpointBedrockRelease;
        }
        CrashManager crashManager = new CrashManager(str, str2, sentryEndpointConfig);
        this.mCrashManager = crashManager;
        crashManager.installGlobalExceptionHandler();
        return this.mCrashManager;
    }
